import React, { useState, useEffect } from 'react';
import RoadmapCard from '../components/RoadmapCard';
import '../styles/Roadmaps.css';

const Roadmaps = () => {
  const [roadmaps, setRoadmaps] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Filter states
  const [audienceFilter, setAudienceFilter] = useState('All');
  const [difficultyFilter, setDifficultyFilter] = useState('All');
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    async function fetchRoadmaps() {
      try {
        const res = await fetch('http://127.0.0.1:5001/api/roadmaps');
        if (!res.ok) {
          throw new Error('Failed to fetch roadmaps');
        }
        const data = await res.json();
        setRoadmaps(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }
    fetchRoadmaps();
  }, []);

  // Filter the fetched roadmaps
  const filteredRoadmaps = roadmaps.filter(roadmap => {
    const matchesAudience =
      audienceFilter === 'All' || roadmap.audience === audienceFilter;
    const matchesDifficulty =
      difficultyFilter === 'All' || roadmap.difficulty === difficultyFilter;
    const matchesSearch =
      roadmap.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      roadmap.description.toLowerCase().includes(searchQuery.toLowerCase());
    return matchesAudience && matchesDifficulty && matchesSearch;
  });

  // Extract unique values for filters from fetched data
  const audiences = ['All', ...new Set(roadmaps.map(r => r.audience))];
  const difficulties = ['All', ...new Set(roadmaps.map(r => r.difficulty))];

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading roadmaps...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <p>{error}</p>
      </div>
    );
  }

  return (
    <div className="roadmaps-page">
      <div className="roadmaps-header">
        <div className="container">
          <h1>AI Learning Paths</h1>
          <p>
            Explore comprehensive learning roadmaps designed to guide you through
            your AI education journey.
          </p>
          <div className="search-bar">
            <input
              type="text"
              placeholder="Search roadmaps..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <button className="search-button">Search</button>
          </div>
        </div>
      </div>

      <div className="container">
        <div className="roadmaps-filters">
          <div className="filter-section">
            <h3>Audience</h3>
            <div className="filter-buttons">
              {audiences.map((audience) => (
                <button
                  key={audience}
                  className={`filter-button ${
                    audienceFilter === audience ? 'active' : ''
                  }`}
                  onClick={() => setAudienceFilter(audience)}
                >
                  {audience}
                </button>
              ))}
            </div>
          </div>

          <div className="filter-section">
            <h3>Difficulty</h3>
            <div className="filter-buttons">
              {difficulties.map((difficulty) => (
                <button
                  key={difficulty}
                  className={`filter-button ${
                    difficultyFilter === difficulty ? 'active' : ''
                  }`}
                  onClick={() => setDifficultyFilter(difficulty)}
                >
                  {difficulty}
                </button>
              ))}
            </div>
          </div>
        </div>

        <div className="active-filters">
          {audienceFilter !== 'All' && (
            <div className="active-filter">
              <span>Audience: {audienceFilter}</span>
              <button
                className="clear-filter"
                onClick={() => setAudienceFilter('All')}
              >
                ×
              </button>
            </div>
          )}

          {difficultyFilter !== 'All' && (
            <div className="active-filter">
              <span>Difficulty: {difficultyFilter}</span>
              <button
                className="clear-filter"
                onClick={() => setDifficultyFilter('All')}
              >
                ×
              </button>
            </div>
          )}

          {searchQuery && (
            <div className="active-filter">
              <span>Search: {searchQuery}</span>
              <button
                className="clear-filter"
                onClick={() => setSearchQuery('')}
              >
                ×
              </button>
            </div>
          )}

          {(audienceFilter !== 'All' ||
            difficultyFilter !== 'All' ||
            searchQuery) && (
            <button
              className="clear-all-filters"
              onClick={() => {
                setAudienceFilter('All');
                setDifficultyFilter('All');
                setSearchQuery('');
              }}
            >
              Clear all filters
            </button>
          )}
        </div>

        <div className="roadmaps-results">
          <h2>
            {filteredRoadmaps.length}{' '}
            {filteredRoadmaps.length === 1 ? 'Roadmap' : 'Roadmaps'}
            {audienceFilter !== 'All' ? ` for ${audienceFilter}` : ''}
            {difficultyFilter !== 'All' ? ` at ${difficultyFilter} level` : ''}
            {searchQuery ? ` matching "${searchQuery}"` : ''}
          </h2>

          <div className="roadmaps-grid">
            {filteredRoadmaps.length > 0 ? (
              filteredRoadmaps.map((roadmap) => (
                <RoadmapCard
                  key={roadmap.id || roadmap._id}
                  roadmap={roadmap}
                />
              ))
            ) : (
              <div className="no-results">
                <h3>No roadmaps found</h3>
                <p>
                  Try adjusting your filters to find what you're looking for.
                </p>
                <button
                  className="button"
                  onClick={() => {
                    setAudienceFilter('All');
                    setDifficultyFilter('All');
                    setSearchQuery('');
                  }}
                >
                  Clear filters
                </button>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Roadmaps;
