import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import BlogCard from '../components/BlogCard';
import RecentBlogs from '../components/RecentBlogs';
import AINewsSection from '../components/AINewsSection';
import '../styles/Home.css';

const Home = () => {
  const [blogs, setBlogs] = useState([]);
  const [news, setNews] = useState([]);

  useEffect(() => {
    // Fetch recent blogs
    fetch('/api/blogs')
      .then(response => response.json())
      .then(data => setBlogs(data.slice(0, 4))) // use the first 4 posts
      .catch(err => console.error('Error fetching blogs:', err));

    // Fetch AI news
    fetch('/api/ai-news')
      .then(response => response.json())
      .then(data => setNews(data))
      .catch(err => console.error('Error fetching AI news:', err));
  }, []);

  return (
    <div className="home-page">
      {/* ... Hero section code remains the same ... */}
      {blogs.length > 0 && <RecentBlogs blogs={blogs} />}
      {news.length > 0 && <AINewsSection news={news} />}
      {/* ... CTA section remains the same ... */}
    </div>
  );
};

export default Home;
