import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import BlogCard from '../components/BlogCard';
import '../styles/Blog.css';

const Blog = () => {
  const [blogs, setBlogs] = useState([]);
  const [activeCategory, setActiveCategory] = useState('all');
  const [activeTag, setActiveTag] = useState(null);

  // Fetch blogs from backend API
  useEffect(() => {
    fetch('/api/blogs')
      .then(response => response.json())
      .then(data => setBlogs(data))
      .catch(err => console.error('Error fetching blogs:', err));
  }, []);

  // Extract categories and tags dynamically from fetched blogs
  const categories = ['all', ...new Set(blogs.map(blog => blog.category))];
  const allTags = blogs.reduce((tags, blog) => {
    if (blog.tags && Array.isArray(blog.tags)) {
      blog.tags.forEach(tag => tags.add(tag));
    }
    return tags;
  }, new Set());
  const tags = Array.from(allTags);

  // Filter blogs based on active category and tag
  const filteredBlogs = blogs.filter(blog => {
    const matchesCategory = activeCategory === 'all' || blog.category === activeCategory;
    const matchesTag = !activeTag || (blog.tags && blog.tags.includes(activeTag));
    return matchesCategory && matchesTag;
  });

  return (
    <div className="blog-page">
      {/* ... header and filters code remains similar ... */}
      <div className="blogs-grid">
        {filteredBlogs.length > 0 ? (
          filteredBlogs.map(blog => (
            <BlogCard key={blog._id} blog={blog} />
          ))
        ) : (
          <div className="no-results">
            <h3>No blog posts found</h3>
            <p>Try adjusting your filters to find what you're looking for.</p>
            <button className="button" onClick={() => {
              setActiveCategory('all');
              setActiveTag(null);
            }}>
              Clear filters
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Blog;
