import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import '../styles/BlogPost.css';

const BlogPost = () => {
  const { blogId } = useParams();
  const [blog, setBlog] = useState(null);
  const [relatedPosts, setRelatedPosts] = useState([]);

  useEffect(() => {
    fetch(`/api/blogs/${blogId}`)
      .then(response => response.json())
      .then(data => {
        setBlog(data);
        // Optionally fetch related posts or filter from existing data
        // For example, you might call another endpoint like `/api/blogs?category=${data.category}`
        fetch(`/api/blogs?category=${data.category}`)
          .then(res => res.json())
          .then(posts => {
            const related = posts.filter(post => post._id !== blogId).slice(0, 3);
            setRelatedPosts(related);
          });
      })
      .catch(err => console.error('Error fetching blog:', err));

    window.scrollTo(0, 0);
  }, [blogId]);

  if (!blog) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading article...</p>
      </div>
    );
  }

  return (
    <div className="blog-post-page">
      {/* ... render blog details and sidebar as before ... */}
    </div>
  );
};

export default BlogPost;
